function calcularNumero(){var e=document.getElementById("info");matarHijos(e);for(var n=nombreANumero(document.getElementById("ingreso").value);String(n).length>1;)n=sumarCifras(n);if(0!=n){let t="explicaciones/"+n+".txt";document.getElementById("resultado").innerHTML=n,cambiaContenido(t,e)}else document.getElementById("resultado").innerHTML="El ingeso no es válido"}function sumarCifras(e){e=String(e);for(var n=0,t=0;t<e.length;t++)n+=parseInt(e[t]);return n}function nombreANumero(e){return e=(e=(e=(e=(e=(e=(e=(e=(e=e.replace(/a|á|i|í|q|j|y/gi,"1")).replace(/b|k|r/gi,"2")).replace(/c|g|l|s/gi,"3")).replace(/d|m|t/gi,"4")).replace(/e|é|h|n/gi,"5")).replace(/u|ú|v|w|x/gi,"6")).replace(/o|ó|z/gi,"7")).replace(/f|p/gi,"8")).replace(/\D/gi,"")}function mostrarArchivoDeNumero(e,n){fetch("/explicaciones/"+e+".txt").then(e=>e.text()).then(e=>{e.split(/\n/).forEach(e=>{parrafo=document.createElement("p"),parrafo.innerHTML=e,n.appendChild(parrafo)})})}function matarHijos(e){for(;e.firstChild;)e.removeChild(e.lastChild)}function cambiaContenido(e,n){var t=new XMLHttpRequest;t.onreadystatechange=function(){if(4==this.readyState&&200==this.status){let e;this.responseText.split(/\n/).forEach(t=>{e=document.createElement("p"),e.innerHTML=t,n.appendChild(e)})}},t.open("GET",e,!0),t.send()}function cambiarFondo(e){$("body").css("background-image","url(assets/"+e+")")}